import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import RBC from '../components/RBC'; // Update import to the correct path of your RBC component

const HotelInfo = ({ match }) => {
  const [hotelDetails, setHotelDetails] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const hotelName = match.params.hotelName;

  useEffect(() => {
    const fetchHotelDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8080/findAndReserve/roomsByHotel?hotelName=${hotelName}`);
        if (!response.ok) throw new Error('Failed to fetch hotel details');

        const data = await response.json();
        console.log('Fetched data:', data); // For debugging purposes
        if (!data || !data.rooms || data.rooms.length === 0) throw new Error('No rooms found for this hotel');

        setHotelDetails(data);
      } catch (error) {
        console.error(error.message);
      }
    };

    fetchHotelDetails();
  }, [hotelName]);

  const handleBookNow = (room) => {
    setSelectedRoom(room);
    setShowPopup(true);
  };

  if (!hotelDetails) {
    return <div>Loading hotel information...</div>;
  }

  return (
    <div>
      <Link to="/about-us" className="back-to-results-link">‚Üê Back to Results</Link>
      <h1>{hotelDetails.hotelName}</h1>
      <p>Hotel Address: {hotelDetails.hotelAddress}</p>
      <h2>Available Rooms:</h2>
      <ul>
        {hotelDetails.rooms.map((room) => (
          <li key={room.id}>
            {room.type} - ${room.price}
            <button onClick={() => handleBookNow(room)}>Book Now</button>
          </li>
        ))}
      </ul>
      {showPopup && (
        <RBC
          room={selectedRoom}
          onClose={() => setShowPopup(false)}
        />
      )}
    </div>
  );
};

export default HotelInfo;
