import React, { useState } from 'react';
import './RoomBookingPopup.css'; // Make sure your CSS file is correctly linked

const RoomBookingPopup = ({ room, onClose }) => {
  // Initialize bookingDetails state with safety checks for room data
  const [bookingDetails, setBookingDetails] = useState({
    hotelChain: room ? room.chainName : '',
    hotelLocation: room ? room.address : '',
    roomsAndGuests: '1 Room, 2 Guests',
    fullName: '',
    email: '',
    // Initialize values for any other fields as needed
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingDetails({
      ...bookingDetails,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here, you would typically process the booking details, e.g., send to a server
    console.log('Booking Details:', bookingDetails);
    onClose(); // Close the popup
  };

  // Ensure room data is available before rendering the form
  if (!room) {
    return null; // Or a loading spinner, or any placeholder you see fit
  }

  return (
    <div className="room-booking-popup-overlay">
      <div className="room-booking-popup">
        <button onClick={onClose} className="close-popup-button">Ã—</button>
        <h2>Book Your Room</h2>
        <form onSubmit={handleSubmit} className="booking-form">
          {/* Form fields */}
          <div className="form-field">
            <label htmlFor="fullName">Full Name</label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              value={bookingDetails.fullName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-field">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={bookingDetails.email}
              onChange={handleInputChange}
              required
            />
          </div>
          {/* Add more fields as needed */}
          <button type="submit" className="submit-booking-btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default RoomBookingPopup;
