import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard';
import FilterPopup from '../components/FilterPopup';
import './FindReserve.css';

const FindReserve = () => {
    const [rooms, setRooms] = useState([]);
    const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);
    const [filters, setFilters] = useState({
        chainName: '',
        location: '',
        capacity: [],
        minPrice: '',
        maxPrice: 10000,
        rating: [],
    });

    useEffect(() => {
        fetchRooms();
    }, [filters]);

    const fetchRooms = async () => {
        try {
            const response = await fetch('http://localhost:8080/findAndReserve/rooms', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(filters),
            });

            if (!response.ok) throw new Error('Failed to fetch rooms');

            const data = await response.json();
            setRooms(Object.values(data).flat());
        } catch (error) {
            console.error("Error fetching rooms:", error);
        }
    };

    const applyFilters = (updatedFilters) => {
        setFilters(updatedFilters); // Update the filters state with the applied filters
    };

    return (
        <div className="find-reserve-container">
            <button onClick={() => setIsFilterPopupOpen(true)} className="filter-button">
                Filter
            </button>
            <div className="rooms-container">
                {rooms.length > 0 ? (
                    rooms.map((room, index) => <RoomCard key={index} room={room} />)
                ) : (
                    <p>No rooms available</p>
                )}
            </div>
            {isFilterPopupOpen && (
                <FilterPopup
                    filters={filters}
                    setFilters={setFilters}
                    applyFilters={applyFilters}
                    onClose={() => setIsFilterPopupOpen(false)}
                />
            )}
        </div>
    );
};

export default FindReserve;
