import React from 'react';
import './FilterPopup.css';

const FilterPopup = ({ onClose, filters, setFilters }) => {
    const handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;
        setFilters({
            ...filters,
            [name]: type === 'checkbox' ? (checked ? [...filters[name], value] : filters[name].filter(item => item !== value)) : value
        });
    };

    const createCheckboxGroup = (title, items, name) => (
        <>
            <h3>{title}</h3>
            {items.map((item, index) => (
                <label key={index}>
                    <input
                        type="checkbox"
                        name={name}
                        value={item}
                        checked={Array.isArray(filters[name]) ? filters[name].includes(item) : false}
                        onChange={handleInputChange}
                    />
                    {item}
                </label>
            ))}
        </>
    );

    const roomCapacities = ['1 person', '2 people', '3 people', '4 people'];
    const hotelChains = ['Sunshine Villa & Bar', 'Sunset Getaway Hotel', 'Serenity Hotel & Spa', 'Ocean Breeze Retreat'];
    const categories = ['1 star hotel', '2 star hotel', '3 star hotel', '4 star hotel', '5 star hotel'];
    const totalRooms = ['< 50 rooms', '50-200 rooms', '200-500 rooms', '> 500 rooms'];
    const prices = ['< $100 per night', '$100 - $200 per night', '$200 - $400 per night', '> $400 per night'];

    return (
        <div className="filter-popup-overlay">
            <div className="filter-popup">
                <h2>Filters</h2>
                <button onClick={onClose} className="close-button">X</button>

                {createCheckboxGroup('Room capacity', roomCapacities, 'capacity')}
                {createCheckboxGroup('Hotel Chain', hotelChains, 'chainName')}
                {createCheckboxGroup('Category of Hotel', categories, 'rating')}
                {createCheckboxGroup('Total number of rooms in hotel', totalRooms, 'location')}
                {createCheckboxGroup('Price', prices, 'minPrice')}

                <div className="filter-actions">
                    <button className="reset-button" onClick={() => setFilters({
                        chainName: '',
                        location: '',
                        capacity: '',
                        minPrice: '',
                        maxPrice: 10000,
                        rating: '',
                    })}>Reset</button>
                    <button className="apply-button" onClick={onClose}>Apply</button>
                </div>
            </div>
        </div>
    );
};

export default FilterPopup;
