import React, { useState } from 'react';
import { useAuth } from '../components/AuthContext'; // Adjust the path as necessary
import './SignInModal.css';

const SignInModal = ({ isVisible, onClose }) => {
  const [userType, setUserType] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const { signIn } = useAuth();

  const handleClose = () => {
    setUserType('');
    setEmail('');
    setPassword('');
    setError('');
    onClose();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:8080/${userType === 'employee' ? 'employees' : 'client'}/authenticate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ssn: email.trim(), password: password.trim() }),
      });

      let responseData = await response.json();

      if (!response.ok) {
        throw new Error('Failed to authenticate');
      }

      if (responseData && responseData.ssn) {
        signIn(userType, responseData.name || 'User'); // Use signIn to update global state
        handleClose(); // Close the modal on success
      }
    } catch (error) {
      setError(error.message);
    }
  };

  if (!isVisible) return null;

  return (
    <div className="search-bar-container">
      <form className="search-bar-form" onSubmit={handleSearch}>
        <div className="search-section">
          <input
            type="text"
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="Search for a destination"
            className="search-input"
          />
        </div>
        <div className="search-section">
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="search-input"
          />
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="search-input"
          />
        </div>
        <div className="search-section">
          <button type="button" onClick={togglePopup} className="search-option-btn">
            1 Room, 1 Guest
          </button>
          {isPopupOpen && <RoomGuestsPopup togglePopup={togglePopup} />}
        </div>
        <div className="search-section">
          <button type="button" className="search-option-btn">
            Special Rates
          </button>
        </div>
        <div className="search-section">
          <button type="submit" className="search-submit-btn">
            Find a Hotel
          </button>
        </div>
      </form>
    </div>
  );
};

export default SearchBar;
