import React, { useState } from 'react';
import './RoomBookingPopup.css';

const RoomBookingPopup = ({ hotel, onClose }) => {
  const [bookingDetails, setBookingDetails] = useState({
    hotelChain: hotel ? hotel.chainName : '',
    hotelLocation: hotel ? hotel.location : '',
    roomsAndGuests: '1 Room, 2 Guests',
    fullName: '',
    email: '',
    checkInDate: '',
    checkOutDate: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Booking Details:', bookingDetails);
    onClose(); // This should close the popup
  };

  if (!hotel) {
    return <div>Loading room details...</div>;
  }

  return (
    <div className="room-booking-popup-overlay">
      <div className="room-booking-popup">
        <button onClick={onClose} className="close-popup-button">Ã—</button>
        <h2>Book Your Room at {hotel.chainName}</h2>
        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-field">
            <label htmlFor="fullName">Full Name:</label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              value={bookingDetails.fullName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-field">
            <label htmlFor="email">Email Address:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={bookingDetails.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-field">
            <label htmlFor="checkInDate">Check-In Date:</label>
            <input
              type="date"
              id="checkInDate"
              name="checkInDate"
              value={bookingDetails.checkInDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-field">
            <label htmlFor="checkOutDate">Check-Out Date:</label>
            <input
              type="date"
              id="checkOutDate"
              name="checkOutDate"
              value={bookingDetails.checkOutDate}
              onChange={handleInputChange}
              required
            />
          </div>
          {/* Include additional fields as needed */}
          <button type="submit" className="submit-booking-btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default RoomBookingPopup;
