import React, { useState } from 'react';
import './NavBar.css';
import { Link } from 'react-router-dom';
import SignInModal from './SignInModal'; // Make sure the path to SignInModal is correct
import { useAuth } from './AuthContext'; // Adjust the import path as necessary

function NavBar() {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const { isAuthenticated, full_name, signOut } = useAuth();

  const toggleModal = () => setIsModalVisible(!isModalVisible);

  return (
    <>
      <nav className="navbar">
        <ul className="navbar-nav">
          <li className="nav-item">
            <Link to="/">HOME</Link>
          </li>
          <li className="nav-item">
            <Link to="/about-us">ABOUT US</Link>
          </li>
          <li className="nav-item">
            <Link to="/find-reserve">FIND & RESERVE</Link>
          </li>
        </ul>
        <ul className="navbar-nav navbar-right">
          {!isAuthenticated ? (
            <>
              <li className="nav-item">
                <Link to="/join-us">Join</Link>
              </li>
              <li className="nav-item" onClick={toggleModal}>
                Sign In
              </li>
            </>
          ) : (
            <>
              <li className="nav-item">
                Welcome, {full_name}
              </li>
              <li className="nav-item" onClick={signOut}>
                Sign Out
              </li>
            </>
          )}
        </ul>
      </nav>
      {!isAuthenticated && <SignInModal isVisible={isModalVisible} onClose={toggleModal} />}
    </>
  );
}

export default NavBar;
