import React, { useState, useEffect } from 'react';
import SearchBar from '../components/SearchBar';
import RoomCard from '../components/RoomCard';
import FilterPopup from '../components/FilterPopup';
import { useSearchParams } from 'react-router-dom';

const FindReserve = () => {
  const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);
  const [rooms, setRooms] = useState([]); // Store the fetched rooms here
  const [searchParams] = useSearchParams();

  useEffect(() => {
    const fetchData = async () => {
      const searchQuery = {
        chainName: searchParams.get('chainName') || "",
        location: searchParams.get('location') || "",
        capacity: parseInt(searchParams.get('capacity')) || 0,
        minPrice: parseFloat(searchParams.get('minPrice')) || 0,
        maxPrice: parseFloat(searchParams.get('maxPrice')) || Infinity,
        rating: parseInt(searchParams.get('rating')) || 0
      };

      try {
        const response = await fetch('http://localhost:8080/findAndReserve/rooms', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(searchQuery),
        });

        if (!response.ok) throw new Error('Network response was not ok');

        const data = await response.json();
        // Assuming the data is an object where each key is a hotel name and the value is an array of room objects
        const roomsArray = Object.values(data).flat(); // Flatten the array of arrays
        setRooms(roomsArray);
      } catch (error) {
        console.error('Failed to fetch rooms:', error);
      }
    };

    fetchData();
  }, [searchParams]); // Depend on searchParams to refetch when they change

  // Assuming room cards expect a flat list of rooms
  return (
    <div className="find-reserve-container">
      <SearchBar />
      <button onClick={() => setIsFilterPopupOpen(true)} className="filter-amenities-button">
        Filter Amenities
      </button>
      {isFilterPopupOpen && (
        <FilterPopup 
          onClose={() => setIsFilterPopupOpen(false)} 
          onApplyFilters={(filters) => console.log('Filters applied:', filters)} // Implement filtering if needed
          availableAmenities={[]} // Compute or fetch available amenities if required
        />
      )}
      <div className="room-listing">
        {rooms.map((room, index) => (
          <RoomCard key={index} room={room} />
        ))}
      </div>
    </div>
  );
};

export default FindReserve;
