import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard'; // Ensure you have this component to display each room
import './FindReserve.css'; // Ensure you have CSS for styling

const FindReserve = () => {
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        // Adjust this URL to match your actual API endpoint for fetching all rooms
        const response = await fetch('http://localhost:8080/findAndReserve/rooms');
        if (!response.ok) {
          throw new Error('Failed to fetch rooms');
        }
        const data = await response.json();
        // Assuming the API returns an array of rooms
        setRooms(data);
      } catch (error) {
        console.error('Error fetching rooms:', error);
      }
    };

    fetchRooms();
  }, []);

  return (
    <div className="find-reserve-container">
      <h1>Available Rooms</h1>
      <div className="rooms-list">
        {rooms.length > 0 ? (
          rooms.map((room, index) => (
            // Assuming the room object has properties you can use in RoomCard
            <RoomCard key={index} room={room} />
          ))
        ) : (
          <p>No rooms available.</p>
        )}
      </div>
    </div>
  );
};

export default FindReserve;
