import React from 'react';
import './AddReservationForm.css'; // Ensure you have the corresponding CSS file

const AddReservationForm = ({ onClose }) => {
  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically handle the reservation data,
    // for example, sending it to a backend API
    console.log('Reservation submitted');
    onClose();
  };

  return (
    <div className="add-reservation-popup">
      <div className="add-reservation-content">
        <form onSubmit={handleSubmit}>
          <h2>Add Reservation</h2>
          {/* Mocked form fields based on the provided design */}
          <label>
            Hotel Chain
            <select>
              <option>Ocean Breeze Retreat</option>
              {/* Add more hotel options here */}
            </select>
          </label>
          <label>
            Location
            <select>
              <option>123 New York Street, NY</option>
              {/* Add more locations here */}
            </select>
          </label>
          {/* Add other form fields as per your design */}
          <button type="submit">Confirm payment</button>
        </form>
        <button onClick={onClose} className="close-btn">X</button>
      </div>
    </div>
  );
};

export default AddReservationForm;
