import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard';
import FilterPopup from '../components/FilterPopup';
import './FindReserve.css'; // Ensure you have the corresponding CSS

const FindReserve = () => {
  const [rooms, setRooms] = useState([]);
  const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);
  const [filters, setFilters] = useState({
    chainName: '',
    location: '',
    rating: 0
  });

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        // Fetch without filters to get all rooms initially
        const response = await fetch('http://localhost:8080/findAndReserve/rooms');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setRooms(data); // Assuming 'data' is an array of room objects
      } catch (error) {
        console.error('Error fetching rooms:', error);
      }
    };

    fetchRooms();
  }, []);

  const applyFilters = () => {
    const applyFilter = (room) => {
      return (
        (filters.chainName ? room.chainName === filters.chainName : true) &&
        (filters.location ? room.location === filters.location : true) &&
        (filters.rating ? room.rating >= filters.rating : true)
      );
    };

    setRooms(rooms.filter(room => applyFilter(room)));
  };

  return (
    <div className="find-reserve-container">
      <button onClick={() => setIsFilterPopupOpen(true)}>Filter Options</button>

      {isFilterPopupOpen && (
        <FilterPopup
          onClose={() => setIsFilterPopupOpen(false)}
          filters={filters}
          setFilters={setFilters}
          applyFilters={applyFilters}
        />
      )}

      <div className="rooms-container">
        {rooms.length > 0 ? (
          rooms.map((room, index) => (
            <RoomCard key={index} room={room} />
          ))
        ) : (
          <p>No rooms available</p>
        )}
      </div>
    </div>
  );
};

export default FindReserve;
