import React from 'react';
import './FilterPopup.css'; // Your CSS file to style the popup

const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {
  return (
    <div className="filter-popup-overlay">
      <div className="filter-popup">
        <h2>Filters</h2>
        <button onClick={onClose} className="close-button">X</button>
        {/* Form elements for each filter */}
        {/* Ensure you have onChange handlers similar to handleChainNameChange for each input */}
        <button onClick={applyFilters} className="apply-filters-button">Apply Filters</button>
      </div>
    </div>
  );
};

export default FilterPopup;
