import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import RBC from '../components/RBC'; // Make sure this path is correct

const HotelInfo = () => {
  const [hotelDetails, setHotelDetails] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null);

  // If you are using hotelName from URL parameters
  // const { hotelName } = useParams();

  // If hotelName is supposed to be a part of the component's props
  // const { hotelName } = props; 

  useEffect(() => {
    const fetchHotelDetails = async () => {
      try {
        const response = await fetch('http://localhost:8080/findAndReserve/roomsByHotel', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ hotelName: 'North Star Mountain Escape' }), // use a variable or prop if dynamic
        });

        if (!response.ok) throw new Error('Failed to fetch hotel details');

        const data = await response.json();
        setHotelDetails(data);
      } catch (error) {
        console.error('Fetch error:', error.message);
      }
    };

    fetchHotelDetails();
  }, []); // Removed the dependency on hotelName; add it back if it's dynamic

  const handleBookNow = (room) => {
    setSelectedRoom(room);
    setShowPopup(true);
  };

  if (!hotelDetails) {
    return <div>Loading hotel information...</div>;
  }

  return (
    <div>
      <Link to="/about-us" className="back-to-results-link">‚Üê Back to Results</Link>
      <h1>{hotelDetails.hotelName}</h1>
      <p>Hotel Address: {hotelDetails.hotelAddress}</p>
      <h2>Available Rooms:</h2>
      <ul>
        {hotelDetails.rooms.map((room) => (
          <li key={room.id}>
            {`${room.type} - $${room.price} per night. Capacity: ${room.capacity}. Amenities: ${room.amenities}`}
            {room.issues && <span> Issues: {room.issues}</span>}
            <button onClick={() => handleBookNow(room)}>Book Now</button>
          </li>
        ))}
      </ul>

      {showPopup && selectedRoom && (
        <RBC
          room={selectedRoom}
          onClose={() => setShowPopup(false)}
        />
      )}
    </div>
  );
};

export default HotelInfo;
