import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingFormPopup from '../components/BookingFormPopup'; // Adjust path as needed
import './HotelInfo.css'; // Ensure the path is correct

const HotelInfo = () => {
  const { hotelName } = useParams();
  const [hotelDetails, setHotelDetails] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHotelChainsAndHotels = async () => {
      try {
        const response = await fetch('http://localhost:8080/hotelchains/getHotelChainsAndHotels');
        if (!response.ok) throw new Error('Failed to fetch hotel chains and hotels');

        const data = await response.json();
        let foundHotel = null;

        // Assuming data structure is { chainName: [hotels] }
        for (const chain of Object.values(data)) {
          foundHotel = chain[0].hotels.find(h => h === hotelName); // Adjust if your structure is different
          if (foundHotel) {
            // Here, reconstruct the hotel details as needed for display
            setHotelDetails({
              ...foundHotel,
              chainName: chain[0].chainName, // Include the chain name in the hotel details
            });
            break;
          }
        }

        if (!foundHotel) throw new Error('Hotel not found');
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchHotelChainsAndHotels();
  }, [hotelName]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!hotelDetails) return <div>Hotel not found</div>;

  return (
    <div className="hotel-info-container">
      <button onClick={() => window.history.back()}>‚Üê Back to results</button>
      {/* Display hotel details, assuming hotelDetails includes the necessary information */}
      <h1>{hotelDetails.hotelName}</h1>
      {/* The rest of your component here */}

      {showBookingForm && (
        <BookingFormPopup 
          hotel={hotelDetails} 
          onClose={() => setShowBookingForm(false)}
        />
      )}
    </div>
  );
};

export default HotelInfo;
