import React, { useState } from 'react';
import './RoomBookingPopup.css';

const RoomBookingPopup = ({ hotel, onClose }) => {
  const [bookingDetails, setBookingDetails] = useState({
    hotelChain: hotel ? hotel.chainName : '',
    hotelLocation: hotel ? hotel.location : '',
    roomsAndGuests: '1 Room, 2 Guests',
    fullName: '',
    email: '',
    checkInDate: '',
    checkOutDate: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Booking Details:', bookingDetails);
    onClose();
  };

  if (!hotel) {
    return <div>Loading room details...</div>;
  }

  return (
    <div className="room-booking-popup-overlay">
      <div className="room-booking-popup">
        <button onClick={onClose} className="close-popup-button">Ã—</button>
        <h2>Book Your Room</h2>
        <form onSubmit={handleSubmit} className="booking-form">
          {/* Assume all inputs and labels are similar to the provided code */}
          <button type="submit" className="submit-booking-btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default RoomBookingPopup;
