import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard';
import FilterPopup from '../components/FilterPopup'; // This component needs to be created

const FindReserve = () => {
  const [rooms, setRooms] = useState([]);
  const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const requestBody = {
          chainName: "",
          location: "",
          capacity: 0,
          minPrice: 0,
          maxPrice: 10000,
          rating: 0
        };

        const response = await fetch('http://localhost:8080/findAndReserve/rooms', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestBody),
        });

        if (!response.ok) {
          throw new Error('Failed to fetch rooms');
        }

        const data = await response.json();
        const roomsArray = Object.values(data).flat();
        setRooms(roomsArray);
      } catch (error) {
        console.error("Error fetching rooms:", error);
      }
    };

    fetchRooms();
  }, []);

  const toggleFilterPopup = () => {
    setIsFilterPopupOpen(!isFilterPopupOpen);
  };

  // Function to apply filters - To be implemented
  const applyFilters = (filters) => {
    console.log('Filters to apply:', filters);
    // Logic to apply filters goes here
  };

  return (
    <div className="find-reserve-container">
      <button onClick={toggleFilterPopup} className="filter-button">
        Filter Options
      </button>
      {isFilterPopupOpen && (
        <FilterPopup
          onClose={toggleFilterPopup}
          onApplyFilters={applyFilters}
        />
      )}
      <div className="rooms-container">
        {rooms.length > 0 ? (
          rooms.map((room, index) => <RoomCard key={index} room={room} />)
        ) : (
          <p>No rooms available.</p>
        )}
      </div>
    </div>
  );
};

export default FindReserve;
