import React, { useState, useEffect } from 'react';
import RoomBookingPopup from './RoomBookingPopup'; // Adjust the import path as necessary

const HotelInfo = ({ hotelName }) => {
  const [hotelDetails, setHotelDetails] = useState(null);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    // Simulate fetching hotel details
    const fetchHotelDetails = async () => {
      // Replace this URL with your actual endpoint to fetch hotel details by name
      const response = await fetch(`http://your-api-endpoint/hotels/${hotelName}`);
      if (!response.ok) {
        console.error("Failed to fetch hotel details");
        return;
      }
      const data = await response.json();
      setHotelDetails(data);
    };

    fetchHotelDetails();
  }, [hotelName]);

  if (!hotelDetails) {
    return <div>Loading hotel information...</div>;
  }

  return (
    <div>
      <h1>{hotelDetails.name}</h1>
      {/* Display other hotel information */}
      <button onClick={() => setShowPopup(true)}>Book a Room</button>

      {showPopup && (
        <RoomBookingPopup 
          room={hotelDetails} 
          onClose={() => setShowPopup(false)} 
        />
      )}
    </div>
  );
};

export default HotelInfo;
