// NavBar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './components/AuthContext'; // Adjust the import path as necessary
import SignInModal from './SignInModal'; // Adjust the import path as necessary

const Navbar = () => {
  const { isAuthenticated, userName, signOut } = useAuth();
  const [isModalVisible, setIsModalVisible] = useState(false);

  const toggleModal = () => setIsModalVisible(!isModalVisible);

  return (
    <>
      <nav className="navbar">
        <Link to="/">Home</Link>
        <Link to="/about-us">About Us</Link>
        <Link to="/find-reserve">Find & Reserve</Link>
        {isAuthenticated ? (
          <div>
            <span>Welcome, {userName}</span>
            <button onClick={signOut}>Sign Out</button>
          </div>
        ) : (
          <button onClick={toggleModal}>Sign In</button>
        )}
      </nav>
      {isModalVisible && <SignInModal isVisible={isModalVisible} onClose={toggleModal} />}
    </>
  );
};

export default Navbar;
