import React, { useState } from 'react';
import './DirectRenting.css';
import AddReservationForm from '../components/AddReservationForm'; // Correct import path

const DirectRenting = () => {
  const [email, setEmail] = useState('');
  const [booking, setBooking] = useState(null);
  const [showAddReservation, setShowAddReservation] = useState(false);

  // Log the state to debug
  console.log({ email, booking, showAddReservation });

  const handleFindBooking = async () => {
    // Mock logic to simulate finding a booking
    console.log('Attempting to find booking for:', email);
    if (email === 'johndoe@example.com') {
      setBooking({
        clientName: 'John Doe',
        clientEmail: 'johndoe@example.com',
        details: 'Mar 12 - Mar 13 2024, 1 Room, 2 Guests',
        price: '215',
      });
      console.log('Booking found');
    } else {
      console.log('No booking found for that email');
    }
  };

  const handleSaveChanges = () => {
    console.log('Saving changes for booking:', booking);
    // Handle save logic here
  };

  const handleDeleteBooking = () => {
    console.log('Deleting booking:', booking);
    setBooking(null);
  };

  const toggleAddReservation = () => {
    setShowAddReservation(!showAddReservation);
  };

  return (
    <div className="direct-renting">
      {/* Find booking section */}
      <div className="search-booking">
        <input
          type="email"
          placeholder="Enter the clientâ€™s email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="email-input"
        />
        <button onClick={handleFindBooking} className="find-booking-btn">
          Find booking
        </button>
      </div>

      {/* Booking details */}
      {booking && (
        <div className="booking-details-card">
          <div className="booking-info">
            <h2>{booking.clientName} - {booking.clientEmail}</h2>
            <p>{booking.details}</p>
            <div className="booking-actions">
              <button onClick={handleSaveChanges} className="action-btn save">Save</button>
              <button onClick={handleDeleteBooking} className="action-btn delete">Delete</button>
            </div>
          </div>

          {/* Payment method form */}
          <div className="payment-method">
            <input placeholder="Credit card number" />
            <input placeholder="Expiration" />
            <input placeholder="CVV" />
            <div className="total">Total ${booking.price} CAD</div>
            <button className="confirm-btn">Confirm payment</button>
          </div>
        </div>
      )}

      {/* Add reservation form */}
      {showAddReservation && <AddReservationForm onClose={toggleAddReservation} />}
    </div>
  );
};

export default DirectRenting;
