import React, { useState } from 'react';
import './RoomCard.css'; // Ensure you have the CSS file at the correct path
import RoomBookingPopup from '../components/RoomBookingPopup'; // Import the RoomBookingPopup component

const RoomCard = ({ room, onBookNow }) => {
    const [isBookingPopupOpen, setIsBookingPopupOpen] = useState(false);

    const handleBookNowClick = () => {
        setIsBookingPopupOpen(true);
        // Call onBookNow function with room data if provided
        if (onBookNow) {
            onBookNow(room);
        }
    };

    return (
        <div className="room-card">
            <h3>{room.hotelName}</h3>
            <div className="room-detail">{room.address}</div>
            <div className="room-rating">Rating: {room.rating} â˜…</div>
            <div className="room-price">Price: ${room.price}</div>
            <div className="room-amenities">Amenities: {room.amenities}</div>
            <button onClick={handleBookNowClick} className="book-now-button">
                Book Now
            </button>

        </div>
    );
};

export default RoomCard;
