import React from 'react';
import './FilterPopup.css'; // Ensure you have the corresponding CSS file

const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {

  const handleChainNameChange = (event) => {
    setFilters({ ...filters, chainName: event.target.value });
  };

  const handleLocationChange = (event) => {
    setFilters({ ...filters, location: event.target.value });
  };

  const handleRatingChange = (event) => {
    setFilters({ ...filters, rating: Number(event.target.value) });
  };

  const handleApplyFilters = () => {
    applyFilters();
    onClose(); // Close the filter popup after applying filters
  };

  return (
    <div className="filter-popup">
      <div className="filter-popup-content">
        <button onClick={onClose} className="close-btn">X</button>
        <h2>Filter Options</h2>

        <label htmlFor="chainName">Hotel Chain:</label>
        <input
          type="text"
          id="chainName"
          value={filters.chainName}
          onChange={handleChainNameChange}
        />

        <label htmlFor="location">Location:</label>
        <input
          type="text"
          id="location"
          value={filters.location}
          onChange={handleLocationChange}
        />

        <label htmlFor="rating">Minimum Rating:</label>
        <select id="rating" value={filters.rating} onChange={handleRatingChange}>
          <option value="0">Any Rating</option>
          <option value="1">1 ★</option>
          <option value="2">2 ★</option>
          <option value="3">3 ★</option>
          <option value="4">4 ★</option>
          <option value="5">5 ★</option>
        </select>

        <button onClick={handleApplyFilters} className="apply-filters-btn">Apply Filters</button>
      </div>
    </div>
  );
};

export default FilterPopup;
