import React, { useState, useEffect } from 'react';
import './BookingFormPopup.css'; // Import the CSS for the popup

const BookingFormPopup = ({ hotel, onClose }) => {
  const [bookingDetails, setBookingDetails] = useState({
    roomType: '', // Initialize without a default room type
    guests: '1',
    rooms: '1',
    fullName: '',
    email: '',
    checkInDate: '',
    checkOutDate: '',
  });

  // Set default values after hotel data is confirmed to be loaded
  useEffect(() => {
    if (hotel && hotel.rooms && hotel.rooms.length > 0) {
      setBookingDetails(prevDetails => ({
        ...prevDetails,
        roomType: hotel.rooms[0].roomType, // Now safe to use the first room type as default
      }));
    }
  }, [hotel]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Booking Details:', bookingDetails);
    // Submission logic here
    onClose(); // Close the popup after submission or on error
  };

  // Check if hotel data is loaded before rendering form
  if (!hotel || !hotel.rooms || hotel.rooms.length === 0) {
    return <div>Loading room details...</div>;
  }

  return (
    <div className="booking-form-popup-overlay">
      <div className="booking-form-popup">
        <button className="close-popup-button" onClick={onClose}>Ã—</button>
        <h2>Reserve Your Room</h2>
        <form onSubmit={handleSubmit}>
          {/* Form fields remain unchanged, except for Room Type dropdown */}
          <div className="form-field">
            <label>Room Type:</label>
            <select
              value={bookingDetails.roomType}
              onChange={(e) => setBookingDetails({ ...bookingDetails, roomType: e.target.value })}
              required // Ensure the user selects a room type if your initial hotel state is undefined
            >
              {hotel.rooms.map((room, index) => (
                <option key={index} value={room.roomType}>{room.roomType}</option>
              ))}
            </select>
          </div>
          {/* Other form fields here */}
          <button type="submit" className="submit-booking-btn">Submit Reservation</button>
        </form>
      </div>
    </div>
  );
};

export default BookingFormPopup;
