import React, { useState, useEffect } from 'react';
import './SearchBar.css'; // Ensure you have the CSS file
import { useHistory } from 'react-router-dom';

const SearchBar = () => {
  const history = useHistory();
  const [destination, setDestination] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        // Assuming hotelsData contains the hotel data you provided
        const cities = hotelsData.map(hotel => {
          const addressParts = hotel.address.split(', ');
          return addressParts[1] || ''; // Assuming city is always the second part
        });
        const uniqueCities = Array.from(new Set(cities)).filter(city => city); // Filter out empty strings
        setLocations(uniqueCities);
      } catch (error) {
        console.error('Fetching cities failed:', error);
      }
    };

    fetchCities();
  }, []);

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Search Details:', { destination, checkIn, checkOut });
    history.push('/find-reserve');
  };

  return (
    <div className="search-bar-wrapper">
      <form className="search-bar" onSubmit={handleSearch}>
        <div className="input-group">
          <label htmlFor="destination" className="input-label">Choose a location</label>
          <select
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            className="input-field"
          >
            <option value="">Select</option>
            {locations.map((location, index) => (
              <option key={index} value={location}>{location}</option>
            ))}
          </select>
        </div>
        <div className="input-group">
          <label htmlFor="check-in" className="input-label">Check-in date</label>
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="input-group">
          <label htmlFor="check-out" className="input-label">Check-out date</label>
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="input-field"
          />
        </div>
        <button type="button" onClick={togglePopup} className="rooms-guests-btn">
          Rooms and Guests
        </button>
        <button type="submit" className="search-button">Find a Hotel</button>
      </form>
      {isPopupOpen && <RoomGuestsPopup togglePopup={togglePopup} />}
    </div>
  );
};

export default SearchBar;
