import React, { useState } from 'react';
import './DirectRenting.css'; // Create this CSS file for styling
import AddReservationForm from '../components/AddReservationForm';

const DirectRenting = () => {
  const [email, setEmail] = useState('');
  const [booking, setBooking] = useState(null);
  const [showAddReservationForm, setShowAddReservationForm] = useState(false);

  const handleFindBooking = async () => {
    // Implement the logic to find booking by client's email
    // This would involve a fetch request to your backend API
    // const response = await fetch('/api/bookings/' + email);
    // const data = await response.json();
    // setBooking(data);

    // Mocked booking data for illustration purposes
    setBooking({
      clientName: 'John Doe',
      clientEmail: 'johndoe@email.com',
      details: 'Mar 12 - Mar 13 2024, 1 Room, 2 Guests',
      price: '215',
    });
  };

  const handleSaveChanges = () => {
    // Logic to save the changes made to the booking
  };

  const handleDeleteBooking = () => {
    // Logic to delete the booking
    setBooking(null);
  };

  return (

    <div className="direct-renting">

      <input
        type="email"
        placeholder="Enter the clientâ€™s email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="email-input"
      />
      <button onClick={handleFindBooking} className="find-booking-btn">
        Find booking
      </button>

      {booking && (
        <div className="booking-details">
          <h2>{booking.clientName} - {booking.clientEmail}</h2>
          <p>{booking.details}</p>
          <button onClick={handleSaveChanges} className="save-btn">Save</button>
          <button onClick={handleDeleteBooking} className="delete-btn">Delete</button>
          {/* Add form elements for payment method details */}
          <div className="payment-method-form">
            <input placeholder="Credit card number" />
            <input placeholder="Expiration" />
            <input placeholder="CVV" />
            <div>Total ${booking.price} CAD</div>
            <button onClick={() => { /* logic to confirm payment */ }} className="confirm-btn">
              Confirm payment
            </button>

          </div>
          <button onClick={() => setShowAddReservationPopup(true)}>Add Reservation</button>


        </div>

      )}
    </div>

  );
};

export default DirectRenting;
