// FilterPopup.js
import React, { useState } from 'react';

const FilterPopup = ({ onClose, onApplyFilters }) => {
  const [filters, setFilters] = useState({
    chainName: '',
    location: '',
    rating: 0,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFilters(prevFilters => ({ ...prevFilters, [name]: value }));
  };

  const handleSubmit = () => {
    onApplyFilters(filters);
    onClose();
  };

  // Render form elements for filter options and a submit button
  return (
    <div className="filter-popup">
      {/* Inputs for chainName, location, and rating */}
      <input name="chainName" value={filters.chainName} onChange={handleInputChange} />
      <input name="location" value={filters.location} onChange={handleInputChange} />
      <input type="number" name="rating" value={filters.rating} onChange={handleInputChange} />
      <button onClick={handleSubmit}>Apply Filters</button>
    </div>
  );
};

export default FilterPopup;
