import React, { useState } from 'react';
import { useAuth } from './AuthProvider'; // Adjust the import path as necessary

const SignInModal = ({ isVisible, onClose }) => {
  const [userType, setUserType] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { signIn } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:8080/${userType === 'employee' ? 'employees' : 'client'}/authenticate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ssn: email.trim(), password: password.trim() }),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(responseData.message || "Authentication failed");
      }

      signIn(userType, responseData.full_name); // Use the full_name from the response
      onClose(); // Close the modal
    } catch (error) {
      console.error("Authentication error:", error.message);
      // Optionally handle error (e.g., show error message to user)
    }
  };

  if (!isVisible) return null;

  // The JSX for your modal goes here
};
