import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import RoomGuestsPopup from './RoomGuestsPopup'; // Make sure to have this component
import './SearchBar.css'; // Ensure your CSS file is linked correctly

const SearchBar = () => {
  const history = useHistory();
  const [cities, setCities] = useState([]);
  const [destination, setDestination] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await fetch('http://localhost:8080/hotelchains/getHotelChainsAndHotels');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();

        const allCities = [];
        Object.values(data).forEach(chainArray => {
          chainArray.forEach(chainItem => {
            chainItem.hotels.forEach(hotel => {
              const addressParts = hotel.address.split(', ');
              const city = addressParts[1]; // Assuming city is always the second part
              if (city && !allCities.includes(city.trim())) {
                allCities.push(city.trim());
              }
            });
          });
        });

        setCities([...new Set(allCities)].sort());
      } catch (error) {
        console.error('Fetching hotel cities failed:', error);
      }
    };

    fetchCities();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    // Add logic to perform the search with the input values
    console.log('Search Details:', { destination, checkIn, checkOut });
    history.push('/find-reserve'); // Redirect to the find and reserve page
  };

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  return (
    <div className="search-bar-wrapper">
      <form className="search-bar" onSubmit={handleSearch}>
        <div className="input-group">
          <label htmlFor="destination" className="input-label">Destination</label>
          <select
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            className="input-field"
            required
          >
            <option value="">Select a city</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>{city}</option>
            ))}
          </select>
        </div>
        <div className="input-group">
          <label htmlFor="check-in" className="input-label">Check-in</label>
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="input-field"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="check-out" className="input-label">Check-out</label>
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="input-field"
            required
          />
        </div>
        <div className="input-group">
          <button type="button" onClick={togglePopup} className="rooms-guests-btn">
            Rooms / Guests
          </button>
          {isPopupOpen && <RoomGuestsPopup togglePopup={togglePopup} />}
        </div>
        <div className="input-group">
          <button type="submit" className="search-button">Search</button>
        </div>
      </form>
    </div>
  );
};

export default SearchBar;
