import React, { useState } from 'react';
import { useAuth } from './components/AuthContext'; // Adjust the import path as necessary

const SignInModal = ({ isVisible, onClose }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('client'); // default to 'client' or manage dynamically
  const { signIn } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const endpoint = userType === 'employee' ? 'employees' : 'clients';
      const response = await fetch(`http://localhost:8080/${endpoint}/authenticate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ssn: email.trim(), password: password.trim() }),
      });

      const responseData = await response.json();
      if (!response.ok) {
        throw new Error(responseData.message || "Authentication failed");
      }

      // Assuming your backend returns the full_name in the response
      signIn(userType, responseData.full_name);
      onClose(); // Assuming onClose is a prop function to close the modal
    } catch (error) {
      console.error("Authentication error:", error.message);
      // Optionally, handle and display error feedback to the user here
    }
  };

  if (!isVisible) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button onClick={onClose} className="close-button">Ã—</button>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="email">SSN:</label>
            <input
              id="email"
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Sign In</button>
        </form>
      </div>
    </div>
  );
};

export default SignInModal;
