import React from 'react';
import './FilterPopup.css';

const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {
    const handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;

        // Convert capacity and rating to numbers as they are initialized as numbers.
        let updatedValue = type === 'checkbox' && checked ? value : value;
        if (name === 'capacity' || name === 'rating') {
            updatedValue = Number(updatedValue);
        }

        setFilters({ ...filters, [name]: updatedValue });
    };

    const applyFiltersHandler = () => {
        applyFilters(filters);
    };

    return (
        <div className="filter-popup-overlay">
            <div className="filter-popup">
                <h2>Filters</h2>
                <button onClick={onClose} className="close-button">X</button>

                {/* Example of handling text input for chainName and location */}
                <div>
                    <label>Hotel Chain</label>
                    <input
                        type="text"
                        name="chainName"
                        value={filters.chainName}
                        onChange={handleInputChange}
                    />
                </div>
                <div>
                    <label>Location</label>
                    <input
                        type="text"
                        name="location"
                        value={filters.location}
                        onChange={handleInputChange}
                    />
                </div>

                {/* Example for handling number input for capacity, minPrice, maxPrice, and rating */}
                <div>
                    <label>Capacity</label>
                    <input
                        type="number"
                        name="capacity"
                        value={filters.capacity}
                        onChange={handleInputChange}
                    />
                </div>
                {/* Add more inputs as needed for minPrice, maxPrice, and rating */}

                <div className="filter-actions">
                    <button className="reset-button" onClick={() => setFilters({
                        chainName: '',
                        location: '',
                        capacity: 0,
                        minPrice: 0,
                        maxPrice: 10000,
                        rating: 0,
                    })}>Reset</button>
                    <button className="apply-button" onClick={applyFiltersHandler}>Apply</button>
                </div>
            </div>
        </div>
    );
};

export default FilterPopup;
