// SearchBar.js
import React, { useState } from 'react';
import RoomGuestsPopup from './RoomGuestsPopup'; // Import your RoomGuestsPopup component
import './SearchBar.css'; // Ensure you have the CSS file

const SearchBar = () => {
  // States for each of the search inputs
  const [destination, setDestination] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  // State for the RoomGuestsPopup visibility
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  // Function to toggle the RoomGuestsPopup
  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  // Your search handling function
  const handleSearch = (e) => {
    e.preventDefault();
    console.log(destination, checkIn, checkOut);
  };

  return (
    <div className="search-bar-container">
      <form className="search-bar-form" onSubmit={handleSearch}>
        <div className="search-input-group">
          <input
            type="text"
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="New York, NY"
            className="search-input-field"
          />
        </div>
        <div className="search-input-group">
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="search-input-field"
          />
        </div>
        <div className="search-input-group">
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="search-input-field"
          />
        </div>
        <button type="button" onClick={togglePopup} className="search-rooms-guests-btn">
          Rooms and Guests
        </button>
        <button type="submit" className="search-find-hotel-btn">
          Find a Hotel
        </button>
      </form>
      {isPopupOpen && <RoomGuestsPopup onClose={togglePopup} />}
    </div>
  );
};

export default SearchBar;
