import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import RBC from '../components/RBC'; // Import RBC component

const HotelInfo = ({ hotelName }) => {
  const [hotelDetails, setHotelDetails] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [error, setError] = useState(null); // State to hold error information

  useEffect(() => {
    const fetchHotelDetails = async () => {
      try {
        const response = await fetch('http://localhost:8080/hotelchains/getHotelChainsAndHotels');
        if (!response.ok) {
          throw new Error('Failed to fetch hotel details');
        }

        const data = await response.json();
        let foundHotel = null;

        Object.values(data).forEach(chains => {
          chains.forEach(chain => {
            const hotel = chain.hotels.find(h => h.name === hotelName);
            if (hotel) {
              foundHotel = { ...hotel, chainName: chain.chainName };
            }
          });
        });

        if (!foundHotel) {
          throw new Error('Hotel not found');
        }

        setHotelDetails(foundHotel);
      } catch (error) {
        setError(error); // Set the error state
        console.error('Error fetching hotel details:', error);
      }
    };

    fetchHotelDetails();
  }, [hotelName]);

  if (error) {
    return <div>Error: {error.message}</div>; // Display error message if there's an error
  }

  if (!hotelDetails) {
    return <div>Loading hotel information...</div>; // Display loading message while fetching data
  }

  return (
    <div>
      <Link to="/about-us" className="back-to-results-link">‚Üê Back to Results</Link> {/* Add this line */}
      <h1>{hotelDetails.name} - {hotelDetails.chainName}</h1>
      <button onClick={() => setShowPopup(true)}>Book a Room</button>
      {showPopup && <RBC hotel={hotelDetails} onClose={() => setShowPopup(false)} />} {/* Updated component */}
    </div>
  );
};

export default HotelInfo;
