import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard';
import FilterPopup from '../components/FilterPopup';

const FindReserve = () => {
  const [rooms, setRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const requestBody = {
          chainName: "",
          location: "",
          capacity: 0,
          minPrice: 0,
          maxPrice: 10000,
          rating: 0
        };

        const response = await fetch('http://localhost:8080/findAndReserve/rooms', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestBody),
        });

        if (!response.ok) {
          throw new Error('Failed to fetch rooms');
        }

        const data = await response.json();
        setRooms(Object.values(data).flat());
        setFilteredRooms(Object.values(data).flat()); // Initially, filteredRooms is the same as rooms
      } catch (error) {
        console.error("Error fetching rooms:", error);
      }
    };

    fetchRooms();
  }, []);

  // Define the applyFilters function here
  const applyFilters = (filters) => {
    // Implement your filter logic based on the filters parameter
    // Example logic (assuming filters has chainName, location, and rating properties):
    const filtered = rooms.filter(room => {
      return (!filters.chainName || room.chainName.includes(filters.chainName)) &&
             (!filters.location || room.address.includes(filters.location)) &&
             (!filters.rating || room.rating >= filters.rating);
    });

    setFilteredRooms(filtered);
  };

  return (
    <div className="find-reserve-container">
      <button onClick={() => setIsFilterPopupOpen(true)} className="filter-button">
        Filter Options
      </button>
      {isFilterPopupOpen && (
        <FilterPopup
          onClose={() => setIsFilterPopupOpen(false)}
          onApplyFilters={applyFilters}
        />
      )}
      <div className="rooms-container">
        {filteredRooms.length > 0 ? (
          filteredRooms.map((room, index) => <RoomCard key={index} room={room} />)
        ) : (
          <p>No rooms available.</p>
        )}
      </div>
    </div>
  );
};

export default FindReserve;
