import React from 'react';
import './FilterPopup.css'; // Your styled CSS file

// Assume filters object has been passed with all the needed properties
const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {
  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;
    // Assuming filters use arrays for checkbox groups
    setFilters({
      ...filters,
      [name]: checked
        ? [...filters[name], event.target.value]
        : filters[name].filter((value) => value !== event.target.value),
    });
  };

  const handleApplyFilters = () => {
    applyFilters(filters);
    onClose(); // Close the filter popup after applying filters
  };

  // Function to render checkboxes for a given category
  const renderCheckboxes = (category, items) => {
    return items.map((item) => (
      <label key={item}>
        <input
          type="checkbox"
          name={category}
          value={item}
          checked={filters[category].includes(item)}
          onChange={handleCheckboxChange}
        />
        {item}
      </label>
    ));
  };

  return (
    <div className="filter-popup-overlay">
      <div className="filter-popup">
        <h2>Filters</h2>
        <button onClick={onClose} className="close-button">X</button>

        <div className="filter-section">
          <h3>Room capacity</h3>
          {renderCheckboxes('roomCapacity', ['1 person', '2 people', '3 people', '4 people'])}
        </div>

        <div className="filter-section">
          <h3>Hotel Chain</h3>
          {renderCheckboxes('hotelChain', [
            'Sunshine Villa & Bar',
            'Sunset Getaway Hotel',
            'Serenity Hotel & Spa',
            'Ocean Breeze Retreat',
          ])}
        </div>

        <div className="filter-section">
          <h3>Category of Hotel</h3>
          {renderCheckboxes('categoryOfHotel', ['1 star hotel', '2 star hotel', '3 star hotel', '4 star hotel', '5 star hotel'])}
        </div>

        <div className="filter-section">
          <h3>Total number of rooms in hotel</h3>
          {renderCheckboxes('totalNumberOfRooms', ['< 50 rooms', '50-200 rooms', '200-500 rooms', '> 500 rooms'])}
        </div>

        <div className="filter-section">
          <h3>Price</h3>
          {renderCheckboxes('price', ['< $100 per night', '$100 - $200 per night', '$200 - $400 per night', '> $400 per night'])}
        </div>

        <div className="filter-actions">
          <button className="reset-button" onClick={() => setFilters({})}>Reset</button>
          <button className="apply-button" onClick={handleApplyFilters}>Apply</button>
        </div>
      </div>
    </div>
  );
};

export default FilterPopup;
