import React, { useState } from 'react';
import { useAuth } from '../components/AuthContext'; // Adjust the path as necessary
import './SignInModal.css';

const SignInModal = ({ isVisible, onClose }) => {
  const [userType, setUserType] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const { signIn } = useAuth();

  const handleClose = () => {
    setUserType('');
    setEmail('');
    setPassword('');
    setError('');
    onClose();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const endpoint = userType === 'employee' ? 'employees' : 'clients';
      const response = await fetch(`http://localhost:8080/${endpoint}/authenticate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ssn: email.trim(), // Make sure email is used as ssn for this example
          password: password.trim(),
        }),
      });

      const text = await response.text(); // First get the text
      let data;

      try {
        data = JSON.parse(text); // Then try to parse it as JSON
      } catch (error) {
        console.error('Failed to parse JSON:', error);
        throw new Error(`Server response was not JSON: ${text}`);
      }

      if (!response.ok) {
        throw new Error(data.message || 'Failed to authenticate');
      }

      // Proceed with authentication success logic...
      console.log('Authentication successful:', data);
      // Update the authentication state, etc.
    } catch (error) {
      console.error('Error during authentication:', error.message);
      // Update your state or UI to display the error message
      setError(error.message);
    }
  };


  if (!isVisible) return null;

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button onClick={handleClose} className="close-button">Ã—</button>
        {error && <div className="error-message">{error}</div>}
        {userType === '' ? (
          <div className="user-type-selection">
            <h2>Select User Type</h2>
            <button onClick={() => setUserType('employee')}>Employee</button>
            <button onClick={() => setUserType('customer')}>Customer</button>
          </div>
        ) : (
          <>
            <h2>Sign In as {userType.charAt(0).toUpperCase() + userType.slice(1)}</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="SSN"
                required
                className="input-field"
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                required
                className="input-field"
              />
              <button type="submit" className="submit-button">Sign In</button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default SignInModal;
