import React, { useState, useEffect } from 'react';
import RoomGuestsPopup from './RoomGuestsPopup'; // Import your RoomGuestsPopup component
import './SearchBar.css'; // Ensure you have the CSS file

const SearchBar = () => {
  const [cities, setCities] = useState([]);
  const [destination, setDestination] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await fetch('http://localhost:8080/hotelchains/getHotelChainsAndHotels');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        const fetchedCities = new Set(); // Use a Set to store unique cities

        Object.values(data).forEach(chains => {
          chains.forEach(chain => {
            chain.hotels.forEach(hotel => {
              const city = hotel.address.split(',')[1].trim(); // Extract the city which is assumed to be after the comma
              fetchedCities.add(city);
            });
          });
        });

        setCities([...fetchedCities]); // Convert the Set to an array
      } catch (error) {
        console.error('There was a problem fetching the hotel cities:', error);
      }
    };

    fetchCities();
  }, []);

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    console.log(destination, checkIn, checkOut);
    // Handle the search logic here
  };

  return (
    <div className="search-bar-wrapper">
      <form className="search-bar" onSubmit={handleSearch}>
        <div className="input-group">
          <label htmlFor="destination" className="input-label">Destination</label>
          <select
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            className="input-field"
          >
            <option value="">Select a city</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>{city}</option>
            ))}
          </select>
        </div>
        <div className="input-group">
          <label htmlFor="check-in" className="input-label">Check-in</label>
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="input-group">
          <label htmlFor="check-out" className="input-label">Check-out</label>
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="input-group">
          <button type="button" onClick={togglePopup} className="rooms-guests-btn">
            Rooms / Guests
          </button>
          {isPopupOpen && <RoomGuestsPopup togglePopup={togglePopup} />}
        </div>
        <div className="input-group">
          <button type="submit" className="search-button">Search</button>
        </div>
      </form>
    </div>
  );
};

export default SearchBar;
