import React from 'react';
import './FilterPopup.css';

const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {
    const handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;

        const updatedValue = Array.isArray(filters[name]) ? [...filters[name]] : [];

        if (type === 'checkbox') {
            if (checked) {
                updatedValue.push(value);
            } else {
                const index = updatedValue.indexOf(value);
                if (index !== -1) {
                    updatedValue.splice(index, 1);
                }
            }
        } else {
            updatedValue[0] = value;
        }

        setFilters({ ...filters, [name]: updatedValue });
    };

    const createCheckboxGroup = (title, items, name) => {
        const filterValues = Array.isArray(filters[name]) ? filters[name] : [];
        return (
            <div key={name}>
                <h3>{title}</h3>
                {items.map((item, index) => (
                    <label key={index}>
                        <input
                            type="checkbox"
                            name={name}
                            value={item}
                            checked={filterValues.includes(item)}
                            onChange={handleInputChange}
                        />
                        {item}
                    </label>
                ))}
            </div>
        );
    };

    const resetFilters = () => {
        setFilters({
            chainName: '',
            location: '',
            capacity: [],
            minPrice: '',
            maxPrice: 10000,
            rating: [],
        });
    };

    return (
        <div className="filter-popup-overlay">
            <div className="filter-popup">
                <h2>Filters</h2>
                <button onClick={onClose} className="close-button">X</button>

                {createCheckboxGroup('Room capacity', ['1 person', '2 people', '3 people', '4 people'], 'capacity')}
                {createCheckboxGroup('Hotel Chain', ['Sunshine Villa & Bar', 'Sunset Getaway Hotel', 'Serenity Hotel & Spa', 'Ocean Breeze Retreat'], 'chainName')}
                {createCheckboxGroup('Category of Hotel', ['1 star hotel', '2 star hotel', '3 star hotel', '4 star hotel', '5 star hotel'], 'rating')}
                {createCheckboxGroup('Total number of rooms in hotel', ['< 50 rooms', '50-200 rooms', '200-500 rooms', '> 500 rooms'], 'location')}
                {createCheckboxGroup('Price', ['< $100 per night', '$100 - $200 per night', '$200 - $400 per night', '> $400 per night'], 'minPrice')}

                <div className="filter-actions">
                    <button className="reset-button" onClick={resetFilters}>Reset</button>
                    <button className="apply-button" onClick={applyFilters}>Apply</button>
                </div>
            </div>
        </div>
    );
};

export default FilterPopup;
