import React, { useState } from 'react';
import RoomGuestsPopup from './RoomGuestsPopup'; // Make sure this path is correct
import './SearchBar.css'; // Make sure this path is correct

const SearchBar = () => {
  const [destination, setDestination] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Implement your search logic here or redirect to the search results page
    console.log(destination, checkIn, checkOut);
    // Example: Redirect to search results
    // history.push(`/search?destination=${destination}&checkIn=${checkIn}&checkOut=${checkOut}`);
  };

  return (
    <div className="search-bar-wrapper">
      <form className="search-bar" onSubmit={handleSearch}>
        <div className="input-group">
          <input
            type="text"
            id="destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="Enter a destination"
            className="input-field"
            aria-label="Enter a destination" // Accessibility improvement
          />
        </div>
        <div className="input-group">
          <input
            type="date"
            id="check-in"
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className="input-field"
            aria-label="Check-in date" // Accessibility improvement
          />
        </div>
        <div className="input-group">
          <input
            type="date"
            id="check-out"
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className="input-field"
            aria-label="Check-out date" // Accessibility improvement
          />
        </div>
        <button
          type="button"
          onClick={togglePopup}
          className="rooms-guests-btn"
          aria-haspopup="true" // Accessibility: Indicates that the button has a popup
          aria-expanded={isPopupOpen} // Accessibility: Indicates if the popup is open
        >
          Rooms and Guests
        </button>
        <button type="submit" className="search-button">
          Find a Hotel
        </button>
      </form>
      {isPopupOpen && (
        <RoomGuestsPopup onClose={togglePopup} /> // Ensure you have an onClose prop in RoomGuestsPopup
      )}
    </div>
  );
};

export default SearchBar;
