import React from 'react';
import './FilterPopup.css';

const FilterPopup = ({ onClose, filters, setFilters, applyFilters }) => {
    const handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;

        const updatedFilters = { ...filters };

        // Handle checkbox input type
        if (type === 'checkbox') {
            const isChecked = checked;
            updatedFilters[name] = isChecked ? [...updatedFilters[name], value] : updatedFilters[name].filter(item => item !== value);
        } else {
            updatedFilters[name] = value;
        }

        setFilters(updatedFilters);
    };

    const applyFiltersHandler = () => {
        applyFilters(filters); // Pass updated filters to the parent component for filtering
    };

    return (
        <div className="filter-popup-overlay">
            <div className="filter-popup">
                <h2>Filters</h2>
                <button onClick={onClose} className="close-button">X</button>

                <h3>Rating</h3>
                <label>
                    <input type="checkbox" name="rating" value="4" checked={filters.rating.includes("4")} onChange={handleInputChange} />
                    4 Stars
                </label>
                <label>
                    <input type="checkbox" name="rating" value="5" checked={filters.rating.includes("5")} onChange={handleInputChange} />
                    5 Stars
                </label>

                {/* Add other filter options as needed */}

                <div className="filter-actions">
                    <button className="reset-button" onClick={() => setFilters({
                        rating: [],
                        // Add other filter options and their default values here
                    })}>Reset</button>
                    <button className="apply-button" onClick={applyFiltersHandler}>Apply</button>
                </div>
            </div>
        </div>
    );
};

export default FilterPopup;
