import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Adjust the import path as necessary

const Navbar = () => {
  const { isAuthenticated, userName, signOut } = useAuth();

  return (
    <nav className="navbar">
      <Link to="/">Home</Link>
      <Link to="/about-us">About Us</Link>
      <Link to="/find-reserve">Find & Reserve</Link>
      {isAuthenticated ? (
        <div>
          <span>Welcome, {userName}</span> {/* Display the user's full name */}
          <button onClick={signOut}>Sign Out</button>
        </div>
      ) : (
        <Link to="/sign-in">Sign In</Link> // Adjust as needed if you're handling sign-in differently
      )}
    </nav>
  );
};

export default Navbar;
