import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard'; // Ensure this is correctly imported
import FilterPopup from '../components/FilterPopup'; // Ensure this is correctly imported
import './FindReserve.css'; // Your CSS file to make it look modern

const FindReserve = () => {
  const [rooms, setRooms] = useState([]);
  const [isFilterPopupOpen, setIsFilterPopupOpen] = useState(false);
  const [filters, setFilters] = useState({
    chainName: "",
    location: "",
    capacity: 0,
    minPrice: 0,
    maxPrice: 10000,
    rating: 0,
  });

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const response = await fetch('http://localhost:8080/findAndReserve/rooms', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filters),
      });

      if (!response.ok) throw new Error('Failed to fetch rooms');

      const data = await response.json();
      setRooms(Object.values(data).flat());
    } catch (error) {
      console.error("Error fetching rooms:", error);
    }
  };

  const applyFilters = () => {
    fetchRooms(); // Re-fetch rooms with the updated filters
    setIsFilterPopupOpen(false); // Close the filter popup
  };

  return (
    <div className="find-reserve-container">
      <button onClick={() => setIsFilterPopupOpen(true)} className="filter-button">
        Filter
      </button>
      <div className="rooms-container">
        {rooms.length > 0 ? (
          rooms.map((room, index) => <RoomCard key={index} room={room} />)
        ) : (
          <p>No rooms available</p>
        )}
      </div>
      {isFilterPopupOpen && (
        <FilterPopup
          filters={filters}
          setFilters={setFilters}
          applyFilters={applyFilters}
          onClose={() => setIsFilterPopupOpen(false)}
        />
      )}
    </div>
  );
};

export default FindReserve;
